mapper.select:
 		select * from(
	 		select l.id,l.name,l.openId,l.consumeCount,l.shareMoney,l.describe,m.logo shopLogo,m.shopName,m.sumShareCount,m.tradeId,l.createTime,
	 		ROUND(
	        6378.138 * 2 * ASIN(
	            SQRT(
	                POW(
	                    SIN(
	                        (
	                            #{myLatitude} * PI() / 180 - latitude * PI() / 180
	                        ) / 2
	                    ),
	                    2
	                ) + COS(40.0497810000 * PI() / 180) * COS(latitude * PI() / 180) * POW(
	                    SIN(
	                        (
	                            #{myLongitude} * PI() / 180 - longitude * PI() / 180
	                        ) / 2
	                    ),
	                    2
	                )
	            )
	        ) * 1000
	    	) AS distance from lease_vip l left join merchant m on l.shopId=m.id
    	)t where 1=1 

 			and t.tradeId=#{tradeId}



 				and distance<=#{end}


 				and distance between #{start} and #{end}


 				and distance between #{start} and #{end}


 				and distance>=#{start}




 				and t.sumShareCount<=#{end}


 				and t.sumShareCount between #{start} and #{end}


 				and t.sumShareCount between #{start} and #{end}


 				and t.sumShareCount>=#{start}




	 		  and t.tradeId=#{tradeId}

	 		order by t.createTime desc,t.sumShareCount desc


 			order by distance 

 				asc


 				desc



 			order by t.sumShareCount 

 				asc


 				desc
